import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [admin, setAdmin] = useState(null);
  const [payments, setPayments] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('pending');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const adminData = localStorage.getItem('admin');
    const token = localStorage.getItem('adminToken');
    
    if (!adminData || !token) {
      navigate('/login');
      return;
    }
    
    setAdmin(JSON.parse(adminData));
    fetchData();
  }, [filter, navigate]);

  const fetchData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('adminToken');
      
      // Set auth header for admin requests
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      // Fetch payments and stats
      const [paymentsRes, statsRes] = await Promise.all([
        api.get(`/admin/payments?status=${filter}`, config),
        api.get('/admin/stats', config)
      ]);

      setPayments(paymentsRes.data.payments);
      setStats(statsRes.data.stats);
      setError('');
    } catch (err) {
      setError('Failed to fetch data');
      console.error('Error:', err);
      
      if (err.response?.status === 401) {
        handleLogout();
      }
    } finally {
      setLoading(false);
    }
  };

  const handleVerify = async (paymentId) => {
    try {
      const token = localStorage.getItem('adminToken');
      await api.patch(
        `/admin/payments/${paymentId}/verify`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      setSuccess('Payment verified successfully!');
      fetchData();
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError('Failed to verify payment');
      console.error('Error:', err);
    }
  };

  const handleReject = async (paymentId) => {
    const reason = prompt('Enter rejection reason (optional):');
    
    try {
      const token = localStorage.getItem('adminToken');
      await api.patch(
        `/admin/payments/${paymentId}/reject`,
        { reason },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      setSuccess('Payment rejected!');
      fetchData();
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError('Failed to reject payment');
      console.error('Error:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('admin');
    navigate('/login');
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatAmount = (amount, currency) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency
    }).format(amount);
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'pending':
        return 'status-badge status-pending';
      case 'verified':
        return 'status-badge status-verified';
      case 'completed':
        return 'status-badge status-completed';
      case 'failed':
        return 'status-badge status-failed';
      default:
        return 'status-badge';
    }
  };

  return (
    <div className="container">
      <div className="card card-large">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
          <h2>üõ°Ô∏è Admin Dashboard</h2>
          <button onClick={handleLogout} className="btn btn-secondary" style={{ width: 'auto', padding: '0.5rem 1.5rem' }}>
            Logout
          </button>
        </div>

        <p style={{ marginBottom: '1.5rem', color: '#666' }}>
          Welcome, <strong>{admin?.fullName}</strong> ({admin?.role})
        </p>

        {/* Stats */}
        {stats && (
          <div className="grid">
            <div className="stat-card">
              <h3>{stats.totalPayments}</h3>
              <p>Total Payments</p>
            </div>
            <div className="stat-card">
              <h3>{stats.pendingPayments}</h3>
              <p>Pending Review</p>
            </div>
            <div className="stat-card">
              <h3>{stats.verifiedPayments}</h3>
              <p>Verified</p>
            </div>
            <div className="stat-card">
              <h3>${stats.totalAmount?.toFixed(2)}</h3>
              <p>Total Processed</p>
            </div>
          </div>
        )}

        {/* Alerts */}
        {success && <div className="alert alert-success" style={{ marginTop: '2rem' }}>{success}</div>}
        {error && <div className="alert alert-error" style={{ marginTop: '2rem' }}>{error}</div>}

        {/* Filter Tabs */}
        <div style={{ marginTop: '3rem', marginBottom: '1rem' }}>
          <h3 style={{ marginBottom: '1rem' }}>Payment Verification</h3>
          
          <div style={{ display: 'flex', gap: '0.5rem', marginBottom: '1.5rem' }}>
            {['pending', 'verified', 'failed', 'all'].map((status) => (
              <button
                key={status}
                onClick={() => setFilter(status)}
                style={{
                  padding: '0.5rem 1rem',
                  border: filter === status ? '2px solid #667eea' : '2px solid #e0e0e0',
                  background: filter === status ? '#667eea' : 'white',
                  color: filter === status ? 'white' : '#666',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '600',
                  textTransform: 'capitalize',
                  transition: 'all 0.3s'
                }}
              >
                {status}
              </button>
            ))}
          </div>
        </div>

        {/* Loading */}
        {loading && <div className="loading">Loading payments...</div>}

        {/* No Payments */}
        {!loading && payments.length === 0 && (
          <div className="text-center" style={{ padding: '2rem', color: '#666' }}>
            <p>No {filter} payments found.</p>
          </div>
        )}

        {/* Payments Table */}
        {!loading && payments.length > 0 && (
          <div style={{ overflowX: 'auto' }}>
            <table className="payment-table">
              <thead>
                <tr>
                  <th>Reference</th>
                  <th>User</th>
                  <th>Payee</th>
                  <th>Amount</th>
                  <th>SWIFT</th>
                  <th>Date</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {payments.map((payment) => (
                  <tr key={payment._id}>
                    <td style={{ fontFamily: 'monospace', fontSize: '0.9rem' }}>
                      {payment.transactionReference}
                    </td>
                    <td>
                      <div>{payment.userId?.fullName}</div>
                      <div style={{ fontSize: '0.85rem', color: '#999' }}>
                        {payment.userId?.username}
                      </div>
                    </td>
                    <td>
                      <div>{payment.payeeName}</div>
                      <div style={{ fontSize: '0.85rem', color: '#999', fontFamily: 'monospace' }}>
                        {payment.payeeAccountNumber}
                      </div>
                    </td>
                    <td style={{ fontWeight: '600' }}>
                      {formatAmount(payment.amount, payment.currency)}
                    </td>
                    <td style={{ fontFamily: 'monospace', fontSize: '0.9rem' }}>
                      {payment.swiftCode}
                    </td>
                    <td style={{ fontSize: '0.9rem' }}>{formatDate(payment.createdAt)}</td>
                    <td>
                      <span className={getStatusBadgeClass(payment.status)}>
                        {payment.status}
                      </span>
                    </td>
                    <td>
                      {payment.status === 'pending' && (
                        <div style={{ display: 'flex', gap: '0.5rem' }}>
                          <button
                            onClick={() => handleVerify(payment._id)}
                            style={{
                              padding: '0.4rem 0.8rem',
                              background: '#38a169',
                              color: 'white',
                              border: 'none',
                              borderRadius: '6px',
                              cursor: 'pointer',
                              fontSize: '0.875rem',
                              fontWeight: '600'
                            }}
                          >
                            ‚úì Verify
                          </button>
                          <button
                            onClick={() => handleReject(payment._id)}
                            style={{
                              padding: '0.4rem 0.8rem',
                              background: '#e53e3e',
                              color: 'white',
                              border: 'none',
                              borderRadius: '6px',
                              cursor: 'pointer',
                              fontSize: '0.875rem',
                              fontWeight: '600'
                            }}
                          >
                            ‚úó Reject
                          </button>
                        </div>
                      )}
                      {payment.status !== 'pending' && (
                        <span style={{ color: '#999', fontSize: '0.875rem' }}>‚Äî</span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
